# coding=utf-8from __future__ import absolute_import, print_functionimport torchimport collections# PATH = '/opt/intern/users/xunwang/checkpoints/bin/jd/512-BN-alpha40/100_model.pth'def multi_gpu_load(check_pt):    model_dict = torch.load(check_pt)    _model_dict = collections.OrderedDict()    for i in model_dict.keys():        new_name = i.split('.', 1)[1]        _model_dict[new_name] = model_dict[i]    return _model_dict